import { useState } from "react";

function App() {
  const [messages, setMessages] = useState([
    { sender: "Tuncay Lore", text: "Sisteme Hoşgeldiniz" }
  ]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userMsg = { sender: "Müşteri", text: input };
    setMessages([...messages, userMsg]);
    setLoading(true);

    const res = await fetch("http://localhost:8000/chat", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ message: input })
    });

    const data = await res.json();
    const botMsg = { sender: "Tuncay Lore", text: data.reply };
    setMessages((prev) => [...prev, botMsg]);
    setInput("");
    setLoading(false);
  };

  return (
    <div style={{ maxWidth: "600px", margin: "auto", padding: "1rem" }}>
      <h2>Tuncay Lore Chat</h2>
      <div style={{ marginBottom: "1rem" }}>
        {messages.map((msg, i) => (
          <div key={i} style={{
            background: msg.sender === "Müşteri" ? "#e0f7fa" : "#f1f1f1",
            textAlign: msg.sender === "Müşteri" ? "right" : "left",
            padding: "10px",
            margin: "10px 0",
            borderRadius: "8px"
          }}>
            <strong>{msg.sender}:</strong> {msg.text}
          </div>
        ))}
        {loading && <div>Yanıt bekleniyor...</div>}
      </div>
      <form onSubmit={sendMessage}>
        <input
          type="text"
          value={input}
          placeholder="Sorunuzu yazın..."
          onChange={(e) => setInput(e.target.value)}
          style={{ width: "75%", padding: "10px" }}
        />
        <button type="submit" style={{ padding: "10px 15px" }}>Gönder</button>
      </form>
    </div>
  );
}

export default App;
